mapper.select:
	select * from staff
	where
		staff_code = #{staff_code}
	and
		staff_password = #{staff_password}

mapper.update:
update shipping
set
	status = #{changeStatus}
where
	orderNum = #{orderNum}

update shipping
set
	goods = #{goods}
	, quantity = #{quantity}
	, deliverydate = #{deliverydate}
	, shop_code = #{shop_code}
	, truck_code = #{truck_code}
where
	orderNum = #{orderNum}
