mapper.insert:
INSERT INTO process values
(
#{processCode}||process_code_seq.NEXTVAL
, SYSDATE
, #{processTerm}
,
#{processEndDate}
, 0
, #{shopCode}
, (SELECT warehouse_code from
warehouse where
#{warehouseName}=warehouse_name)
, (SELECT staff_code
from staff where #{staffName}=staff_name)
)

mapper.select:
SELECT
k.kpiDivision_code, k.kpi_amount FROM kpidivision d INNER JOIN kpi k
ON d.kpiDivision_code = k.kpiDivision_code
AND k.shop_code =
#{shopCode}
AND TO_CHAR(kpi_start_date, 'YYYY-MM') = #{date}
